import React from 'react';
import '../../App.css';
import './Section2.css';
import './Intro.css';
import { useState } from 'react';

function Section2() { 
    const isMobile = /Mobi/.test(navigator.userAgent);
    const [section, setSection] = useState(0);
    const numOfSelection=2;

    function moveSelectionUp(){
        const current = document.getElementsByName(section);

        if(section+1 < numOfSelection){
            const next = document.getElementsByName(section+1);
            next[0].className = "fade-in";

            setSection(section+1);
        }
        current[0].className = "fade-out";

        setTimeout(function() {
            if(section+1 < numOfSelection){
                const next = document.getElementsByName(section+1);
                next[0].className = "fade-in";

                setSection(section+1);
            }
            else{
                const next = document.getElementsByName(0);
                next[0].className = "fade-in";
                setSection(0);
            }
        }, 500);

    }

    function moveSelectionDown(){
        const current = document.getElementsByName(section);
        if(section-1 > 0){
            setSection(section-1);
        }
        else{
            setSection(numOfSelection-1);
        }
    }

  if(isMobile){
    return (
      <div className="about-me-container">

      </div>
    );
  }

  return (
    <div className='MeSection'>
        <section>
            <div className='image'>
                <img src="images/BrianPic.jpg" alt="" className='imge'/>
            </div>

        </section>
        <section className='list'>
            <div onClick={moveSelectionUp}  className='before-btn'>
                <i class="fa-solid fa-chevron-right"></i>
            </div>

            <div className='content'>
            {section === 0 && (
            <div name={section} id='section1'>
                <ul>
                    <li>Computer Science at the University of Florida</li>
                    <li>Web Developer</li>
                    <li>Mobile Developer</li>
                </ul>
            </div>
        )}
        {section === 1 && (
            <div name={section} id='section2' >
                <h2>Languages</h2>
                <div>
                    <li>C++</li>
                    <li>C</li>
                    <li>JavaScript</li>
                    <li>HTML5</li>
                    <li>CSS</li>
                    <li>LESS</li>
                </div>
            </div>
        )}
            </div>

            <div onClick={moveSelectionDown} className='next-btn'>
                <i  class="fa-solid fa-chevron-right"></i>
            </div>
        </section>

    </div>
);
}

export default Section2;
