import React from 'react';
import './AboutMe2.css';

function AboutMe2() {
  const isMobile = /Mobi/.test(navigator.userAgent);
  document.addEventListener('DOMContentLoaded', function(){
    document.querySelector('.Me-title').style.animationPlayState = 'paused';
    tile1s = document.querySelectorAll('#tile1');
    tile2s = document.querySelectorAll('#tile2');

    for(i=0; i<tile1s.length(); i++){
        tile1s[i].style.animationPlayState = 'paused';
        tile1s[2].style.animationPlayState = 'paused';
    }
  })
  window.onscroll = () => {
    // console.log(window.scrollY);
    tile1s = document.querySelectorAll('#tile1');
    tile2s = document.querySelectorAll('#tile2');
    if (window.innerHeight + window.scrollY >= document.body.offsetHeight) {
        // Change color to green
        document.querySelector('.Me-title').style.animationPlayState = 'running';
        for(i=0; i<tile1s.length(); i++){
            tile1s[i].style.animationPlayState = 'running';
            tile1s[2].style.animationPlayState = 'running';
        }
    }
    else{
        document.querySelector('.Me-title').style.animationPlayState = 'paused';
        for(i=0; i<tile1s.length(); i++){
            tile1s[i].style.animationPlayState = 'paused';
            tile1s[2].style.animationPlayState = 'paused';
        }
        }
  }

  if(isMobile){
    return (
      <div>
        none
      </div>
    );
  }
  return (

    <div className="ABMC">
        <h1 className='Me-title'>About Me</h1>
        <div id='tile1'>
            <h2>Education: University of Florida</h2>
        </div>
        <div id='tile2'>
            <h2>Bachelors in Computer </h2>
        </div>
        <div id='tile1'>
            <h2> Upper Division GPA: 3.53</h2>
        </div>
        <div id='tile2'>
            <h2> test</h2>
        </div>
    </div>
  );
}

export default AboutMe2;
