import React from 'react';
import { useState, useRef, useEffect } from 'react';
import './Projects.css';
import PopUp from "./PopUp";
import Card from './Card';

const description = new Map([
    ["Ecommerce Website", {"Descrption": "wegnfiw rgning", "Link" : "www.poo.com"}],
    ["Schedule Chomper", {"Descrption": "wegnfiw rgning", "Link" : "www.poo.com"}],
    ["Schedule Chomper", {"Descrption": "wegnfiw rgning", "Link" : "www.poo.com"}]
  ]);

function projects() {
const [visible, setVisible] = useState(false);
const [selected, setSelected] = useState('');

let menuRef = useRef();

useEffect(() =>{
    let handler = (e) => {
        if(!menuRef.current.contains(e.target)){
            setVisible(!visible);
            setSelected('');
        }
      };
    document. addEventListener ("mousedown", handler);
})
  return (
<div id='page'>
{visible ? 
    <div className='pop-up'> 
        <div className="info" ref={menuRef}>
            <button>test</button>
        </div>
    </div>
: null}
    <div id='body'>
        <ul className='wrapper'>
        <Card
            src='images/Auction.png'
            text='Ecommerce Website'
            path='https://github.com/BrianBFarias/ecommerce-website.git'
            setVisible={setVisible}

        />
        <Card
            src='images/Schedule.jpg'
            text='Schedule Chomper'
            path='https://github.com/BrianBFarias/Schedule-Chomper'
            setVisible={setVisible}
        />
        <Card
            src='images/Schedule.jpg'
            text='Schedule Chomper'
            path='https://github.com/BrianBFarias/Schedule-Chomper'
            setVisible={setVisible}

        />
        </ul>
  </div>
  </div>
  );
}

export default projects;
