import React, { useState, useRef, useEffect } from 'react';
import './Projects.css';
import PopUp from "./PopUp";
import Card from './Card';

function Projects() {
  const [visible, setVisible] = useState(false);
  const [selected, setSelected] = useState('');
  const [image, setImage] = useState('');
  const [descriptor, setDescriptor] = useState('');
  const [link, setLink] = useState('');
  const [tech, setTech] = useState('');

  const menuRef = useRef();

  useEffect(() => {
    if(!visible){
        return
    }
    let handler = (e) => {

      if (!menuRef.current.contains(e.target)) {
        setVisible(false);
        setSelected('');
      }
    };
    document.addEventListener("mousedown", handler);

    // Cleanup the event listener on component unmount
    return () => {
      document.removeEventListener("mousedown", handler);
    };
  }, [visible]);

  return (
    <div id='page'>
      {visible ?
        <div className='pop-up'>
          <div className="info" ref={menuRef}>
            <i class="fa-solid fa-xmark" id='close' onClick={() => setVisible(false)}></i>
            <PopUp
            image = {image}
            title={selected}
            description = {descriptor}
            link = {link}
            tech = {tech}
            />
          </div>

        </div>
        : null}
      <div id='body'>
        <ul className='wrapper'>
          <Card
            src='images/Auction.png'
            text='Ecommerce Website'
            path='https://github.com/BrianBFarias/ecommerce-website.git'
            description = "This is a test"
            tech = ""
            setVisible={setVisible}
            setSelected={setSelected}
            setImage = {setImage}
            setLink = {setLink}
            setTech = {setTech}
            setDescriptor = {setDescriptor}
            color = {'light'}
          />
          <Card
            src='images/Schedule.jpg'
            text='Schedule Chomper'
            path='https://github.com/BrianBFarias/Schedule-Chomper'
            description = ""
            tech = "C++/SFML/Python"
            setVisible={setVisible}
            setSelected={setSelected}
            setImage = {setImage}
            setLink = {setLink}
            setTech = {setTech}
            setDescriptor = {setDescriptor}
            color = {'light'}
          />
            <Card
            src='images/network.jpg'
            text='Student Info AVL Tree'
            path='https://github.com/BrianBFarias/Schedule-Chomper'
            description = ""
            tech = "HTML/Django/Python/CSS/Javascript"
            setVisible={setVisible}
            setSelected={setSelected}
            setImage = {setImage}
            setLink = {setLink}
            setTech = {setTech}
            setDescriptor = {setDescriptor}
            color = {'dark'}

          />
          <Card
            src='images/AVL.jpg'
            text='Student Info AVL Tree'
            path='https://github.com/BrianBFarias/Schedule-Chomper'
            description = ""
            tech = "C++"
            setVisible={setVisible}
            setSelected={setSelected}
            setImage = {setImage}
            setLink = {setLink}
            setTech = {setTech}
            setDescriptor = {setDescriptor}
            color = {'dark'}
          />
          <Card
            src='images/Pakudex.jpg'
            text='Pakudex'
            path='https://github.com/BrianBFarias/Schedule-Chomper'
            description = ""
            tech = "C++"
            setVisible={setVisible}
            setSelected={setSelected}
            setImage = {setImage}
            setLink = {setLink}
            setTech = {setTech}
            setDescriptor = {setDescriptor}
            color = {'light'}
          />
          <Card
            src='images/PageRank.jpg'
            text='Student Info AVL Tree'
            path='https://github.com/BrianBFarias/Schedule-Chomper'
            description = ""
            tech = "C++"
            setVisible={setVisible}
            setSelected={setSelected}
            setImage = {setImage}
            setLink = {setLink}
            setTech = {setTech}
            setDescriptor = {setDescriptor}
            color = {'dark'}
          />

        </ul>
      </div>
    </div>
  );
}

export default Projects;
