import React from 'react';
import './AboutMe2.css';

function AboutMe2() {
  const isMobile = /Mobi/.test(navigator.userAgent);
  document.addEventListener('DOMContentLoaded', function(){
    document.querySelector('.Me-title').style.animationPlayState = 'paused';
    document.querySelectorAll('#tile1')[0].style.animationPlayState = 'paused';
    document.querySelectorAll('#tile2').style.animationPlayState = 'paused';
  })
  window.onscroll = () => {
    // console.log(window.scrollY);
    console.log(window.innerHeight + window.scrollY >= document.body.offsetHeight-600);
    if (window.innerHeight + window.scrollY >= document.body.offsetHeight) {
        // Change color to green
        document.querySelector('.Me-title').style.animationPlayState = 'running';
        document.querySelectorAll('#tile1')[0].style.animationPlayState = 'running';
        document.querySelectorAll('#tile2')[0].style.animationPlayState = 'running';
    }
    else{
        document.querySelector('.Me-title').style.animationPlayState = 'paused';
        document.querySelectorAll('#tile1')[0].style.animationPlayState = 'paused';
        document.querySelectorAll('#tile2')[0].style.animationPlayState = 'paused';
        }
  }

  if(isMobile){
    return (
      <div>
        none
      </div>
    );
  }
  return (

    <div className="ABMC">
        <h1 className='Me-title'>About Me</h1>
        <div id='tile1'>
            <h2>Education: University of Florida</h2>
        </div>
        <div id='tile2'>
            <h2>Bachelors in Computer </h2>
        </div>
        <div id='tile1'>
            <h2> Upper Division GPA: 3.53</h2>
        </div>
        <div id='tile2'>
            <h2> test</h2>
        </div>
    </div>
  );
}

export default AboutMe2;
