import React, { useState, useRef, useEffect } from 'react';
import './Projects.css';
import PopUp from "./PopUp";
import Card from './Card';

const description = new Map([
  ["Ecommerce Website", "poopwd wrgiwrhbgiq wrgihbrg"],
  ["Schedule Chomper", "poopwd wrgiwrhbgiq wrgihbrg"],
  ["Schedule Chomper", "poopwd wrgiwrhbgiq wrgihbrg"]
]);

function Projects() {
  const [visible, setVisible] = useState(false);
  const [selected, setSelected] = useState('');
  const [image, setImage] = useState('');
  const [Descriptor, setDescriptor] = useState('');

  const menuRef = useRef();

  useEffect(() => {
    if(!visible){
        return
    }
    let handler = (e) => {

      if (!menuRef.current.contains(e.target)) {
        setVisible(false);
        setSelected('');
      }
    };
    document.addEventListener("mousedown", handler);

    // Cleanup the event listener on component unmount
    return () => {
      document.removeEventListener("mousedown", handler);
    };
  }, [visible]);

  return (
    <div id='page'>
      {visible ?
        <div className='pop-up'>
          <div className="info" ref={menuRef}>
            <i class="fa-solid fa-xmark" id='close' onClick={() => setVisible(false)}></i>
            <PopUp

            />
          </div>

        </div>
        : null}
      <div id='body'>
        <ul className='wrapper'>
          <Card
            src='images/Auction.png'
            text='Ecommerce Website'
            path='https://github.com/BrianBFarias/ecommerce-website.git'
            setVisible={setVisible}
            setSelected={setSelected}
            setImage = {setImage}
          />
          <Card
            src='images/Schedule.jpg'
            text='Schedule Chomper'
            path='https://github.com/BrianBFarias/Schedule-Chomper'
            setVisible={setVisible}
            setSelected={setSelected}
            setImage = {setImage}
          />
          <Card
            src='images/Schedule.jpg'
            text='Schedule Chomper'
            path='https://github.com/BrianBFarias/Schedule-Chomper'
            setVisible={setVisible}
            setSelected={setSelected}
            setImage = {setImage}
          />
        </ul>
      </div>
    </div>
  );
}

export default Projects;
